import React, { useState, useEffect } from "react";
import Display from "./Display";
import ButtonGrid from "./ButtonGrid";
import calculate from "../operations/calculate";
import "./App.css";
import socketIOClient from "socket.io-client";
const ENDPOINT = "http://127.0.0.1:4001";
let socket;
const App = () => {
  const [total, setTotal] = useState(0);
  const [equation, updateEquation] = useState([]);
  const [responses, setResponses] = useState([]);
  const [username, setUsername] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);

  const join = () => {
    if (username !== "") {
      this.setUsername(username);
      setLoggedIn(true);
    }
  };

  const handleClick = (buttonName) => {
    console.log("Click handler pressed " + buttonName);
    // Calculate and update state as required
    const expression = calculate(
      { total, equation, setTotal, updateEquation },
      buttonName
    );
    if (expression !== "") {
      console.log("Solved ", expression);
      socket.emit("data", expression);
      setResponses((prevResponses) => {
        if (prevResponses.length === 10) {
          prevResponses.shift();
        }
        return [...prevResponses, expression];
      });
    }
  };

  useEffect(() => {
    socket = socketIOClient(ENDPOINT);
    socket.on("new user", (name) => setUsername(name));
    socket.on("new message", (data) => {
      console.log("New equation received ", data);
      setResponses((prevResponses) => {
        if (prevResponses.length === 10) {
          prevResponses.shift();
        }
        return [...prevResponses, data.data];
      });
    });
  }, []);

  return (
    <div className="component-app">
      <h1>{username}</h1>
      <Display
        value={
          equation === undefined || equation.length === 0
            ? total
            : equation.join("")
        }
      />
      <ButtonGrid clickHandler={handleClick} />
      {responses.map((response, index) => (
        <Display key={index} value={response} />
      ))}
    </div>
  );
};

export default App;

