import React from "react";
import "./Button.css";

const Button = (props) => {
  handleClick = () => {
    this.props.clickHandler(this.props.name);
  };

  return (
    <div className={this.props.isOperation ? "button-orange" : "button"}>
      <button onClick={this.handleClick}>{props.value}</button>
    </div>
  );
};

export default Button;
